mapper.select:
Select * from ( WITH tab AS ( SELECT num,title,writer,TO_CHAR(DAY,'YYYY-MM-DD') AS daytostring,progress,startday,endday ,(Select count(1) from tbl_projectadmin Where
and(startday>=TO_DATE(#{btnDaySearch1}))
and(endday<=TO_DATE(#{btnDaySearch2})+1)
and(startday>=TO_DATE(#{btnDaySearch1}) and endday<=TO_DATE(#{btnDaySearch2})+1)
) as cnt FROM tbl_projectadmin Where
and(startday>=TO_DATE(#{btnDaySearch1}))
and(endday<=TO_DATE(#{btnDaySearch2})+1)
and(startday>=TO_DATE(#{btnDaySearch1}) and endday<=TO_DATE(#{btnDaySearch2})+1)
) Select /*+INDEX_DESC(tbl_projectadmin pk_projectadmin) */ ROWNUM rn,num,title,writer,daytostring,progress,startday,endday,CNT from (select * from tab order by progress asc,num desc)
Where rownum <= #{pageNum} * #{amount}
order by progress asc,num desc )
where rn >(#{pageNum}-1) * #{amount}

Select * from ( WITH tab AS ( SELECT p.num, title, content, userid, username, TO_CHAR(DAY,'YYYY-MM-DD') AS daytostring, startday, endday, progress ,(Select count(1) from tbl_projectadmin p left outer join tbl_memberselect m on p.num=m.num Where
and userid=#{userid, jdbcType=VARCHAR} and progress=0) as cnt FROM tbl_projectadmin p left outer join tbl_memberselect m on p.num=m.num Where
) Select /*+INDEX_DESC(tbl_projectadmin p left outer join tbl_memberselect m on p.num=m.num) */ ROWNUM rn, num, title, content, userid, username, daytostring, startday, endday, progress, cnt from tab
Where rownum <= #{pageNum} * #{amount}
and userid=#{userid, jdbcType=VARCHAR} and progress=0 )
where rn >(#{pageNum}-1) * #{amount}
order by startday, endday, num asc

select num, title, content,
startday, endday
from tbl_projectadmin Where num = #{num}

Select * from ( WITH tab AS ( SELECT a.* ,(Select filename from TBL_REPORT Where num=a.num and WRITER=b.userid) as filename ,(Select count(1) from tbl_projectadmin a, TBL_MEMBERSELECT b Where a.num=b.num and userid=#{userid}
and(title like '%'||#{keyword}||'%')
and(a.startday>=TO_DATE(#{btnDaySearch1}))
and(a.endday<=TO_DATE(#{btnDaySearch2})+1)
and(a.startday>=TO_DATE(#{btnDaySearch1}) and a.endday<=TO_DATE(#{btnDaySearch2})+1)
) as cnt ,b.userid as userid ,to_char(a.day,'yyyy-mm-dd') as projectday FROM tbl_projectadmin a join TBL_MEMBERSELECT b on a.num=b.num Where b.userid= #{userid}
and(title like '%'||#{keyword}||'%')
and(a.startday>=TO_DATE(#{btnDaySearch1}))
and(a.endday<=TO_DATE(#{btnDaySearch2})+1)
and(a.startday>=TO_DATE(#{btnDaySearch1}) and a.endday<=TO_DATE(#{btnDaySearch2})+1)
order by filename desc, a.num desc ) Select /*+INDEX_DESC(tbl_projectadmin pk_projectadmin) */ ROWNUM rn,num,title,filename,writer,progress,projectday,startday,endday,userid,CNT from tab
Where rownum <= #{pageNum} * #{amount} order by filename desc, num desc )
where rn > (#{pageNum}-1) * #{amount}
